CREATE ALGORITHM=UNDEFINED DEFINER=`shasku5d_shask`@`localhost` SQL SECURITY DEFINER VIEW `shasku5d_shask`.`vw_groups` AS select `g`.`id` AS `id_group`,`g`.`kurs` AS `Курс`,`g`.`name` AS `Наименование`,concat_ws(' ',`s`.`code`,`s`.`name`) AS `Специальность` from `shasku5d_shask`.`groups` as `g` inner join `shasku5d_shask`.`spec` as `s` on((`g`.`id` = `s`.`id`))) order by `g`.`name`